select-sql.select:
SELECT DISTINCT (select u.`name` ConfirmorName from core_paymentplan pp, system_user u where u.state=0 and pp.state=0 and pp.OrderId=o.id and (pp.ConfirmorId is not NULL) and pp.ConfirmorId=u.Id limit 1) ConfirmorName, ca.bank bankName, ca.number bankNumber, ca.bankBranchName, pj.TypeId ProjectTypeId, o.* FROM view_order o left join crm_customeraccount ca on ca.state=0 and ca.id=o.accountId left join system_user u on u.state=0 and o.referralCode = u.referralCode left join crm_production p on p.state=0 and p.id=o.productionId left join crm_productionhome phome on phome.state=0 and phome.id=p.ProductHomeId left join crm_project pj on pj.state=0 and pj.id=phome.ProjectId WHERE 1 = 1
and o.customerId =#{customerId}
and o.salesmanId =#{salesmanId}
and o.status in (0)
and o.status in (7,1,8,23,24,25)
and o.status in (5)
and o.referralCode=#{referralCode}
and o.salesmanName like #{salesmanName,type='%c%'}
and o.PayTime>=#{payTimeStart}
and o.PayTime<=#{payTimeEnd}
ORDER BY o.PayTime DESC
