select-sql.select:
SELECT DISTINCT plan.id, c.`Name` customerName, p.name productionName, o.PayTime, o.money, plan.CurrentInstallment, plan.PaymentTime, plan.TotalPaymentPrincipalMoney, plan.TotalProfitMoney, p.interestCycle, p.interestTimes, p.interestUnit FROM core_paymentplan plan LEFT JOIN v_customer_saleman c on c.state=0 and c.id=plan.CustomerId LEFT JOIN crm_production p on p.state=0 and plan.ProductId=p.id LEFT JOIN crm_order o on o.state=0 and o.id=plan.OrderId WHERE 1=1 And plan.state = 0
and DATE_FORMAT(PaymentTime, '%Y-%m')=#{paymentTime}
order by plan.PaymentTime

SELECT plan.id, date_format(plan.paymentTime, '%Y-%m-%d') AS paymentTime, count(plan.orderId) AS totalRecord, round(sum(plan.totalpaymentPrincipalMoney),2) AS totalpaymentPrincipalMoney, round(sum(plan.totalProfitMoney),2) AS totalProfitMoney, round(sum(plan.paiedPrincipalMoney),2) AS paiedPrincipalMoney, round(sum(paiedProfitMoney),2) AS paiedProfitMoney, ppc.checkName, ppc.checkTime, ppc.checkName2, ppc.checkTime2 FROM core_paymentplan plan left join crm_order o on o.state = 0 and o.`Status` in (1,8) AND o.ProductionId = plan.ProductId AND plan.OrderId = o.id left join crm_production proc on proc.state = 0 AND plan.productId = proc.id left join core_paymentplan_check ppc on ppc.state=0 and DATE_FORMAT(plan.PaymentTime,'%Y-%m')=DATE_FORMAT(ppc.PaymentPlanDate,'%Y-%m') where 1 = 1 AND plan.state = 0
AND plan.`Status` = #{paymentPlanStatus}
AND proc.`Name` LIKE #{productionName,type='%c%'}
AND plan.PaymentTime >= #{startTime}
AND plan.PaymentTime <= #{endTime}
GROUP BY plan.paymentTime ORDER BY plan.paymentTime
